 *  赵明 或 赖建红 或田祖红
 */
(userInfo) => {
    const { user_id } = userInfo;
    const userIdEqFun = (userIdArr) => {
        if(userIdArr.indexOf(user_id)==-1){
            return false;
        }else{
            return true;
        }
