    if( levelGtFun(8) || (levelGtFun(4)&&branchEqFun(['管理部'])) || (levelGtFun(4)&&branchEqFun(['客户关系部'])) || userIdEqFun(['1411','1101','2006']) ){
        return [1,2,3];
    }else{
        return [4];
    }
}

